package org.launchcode.codingevents.controllers;
//displays a list of static events

import org.springframework.stereotype.Controller;
import org.springframework.ui.Model;
import org.springframework.web.bind.annotation.GetMapping;
import org.springframework.web.bind.annotation.PostMapping;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestParam;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

@Controller
@RequestMapping("events")
public class EventController {

    /*public static ArrayList<Object> getEvents() {
        return events;
    }

    public static void setEvents(ArrayList<Object> events) {
        EventController.events = events;
    }*/

    public static ArrayList<Events> events = new ArrayList<Events>();


    @GetMapping
    public String displayAllEvents(Model model) {
        Events eventOne = new Events("Code With Pride", "A fun meetup sponsored by LaunchCode", "12345 South 6th Street", "data:image/jpeg;base64," );
        Events eventTwo = new Events("Menteaship", "A fun meetup for connecting with mentors","435 Funtime avenue","data:image/jpeg;base64," );
        Events eventThree = new Events("Javascripty", "An imaginary meetup for Javascript developers", "5000 indiana blvd","data:image/jpeg;base64,");
        events.add(eventOne);
        events.add(eventTwo);
        events.add(eventThree);
        model.addAttribute("events", events);
        return "events/index";
    }

    @GetMapping("create")
    public String displayCreateEventForm(Model model) {
        model.addAttribute("title", "Create Event");
        return "events/create";
    }

    @PostMapping("create")
    public String processCreateEventForm(@RequestParam String eventName, String eventDescription, String address, String imagePath) {
        Events newEvent = new Events(eventName, eventDescription, address, imagePath);
        events.add(newEvent);
        return "redirect:";
    }

}
